	        //选择头中部菜单事件
	        function selectList(status){
	        	var $menuBut = $('menuBut');
	        	$('main_popupmenu').close();
	        	switch (status){
	        		case '0' :
	        			//$menuBut.css('display','');
	        			$('wait_process_file').css('display','');
	        			$('wait_read_file').css('display','none');
	        			$('processed_file').css('display','none');
	        			$('my_file').css('display','none');
		        		titleText = '待办文件';
		        		break;
		        	case '2' :
		        		//$menuBut.css('display','none');
		        		$('wait_process_file').css('display','none');
	        			$('wait_read_file').css('display','');
	        			$('processed_file').css('display','none');
	        			$('my_file').css('display','none');
		        		titleText = '待阅文件';
		        		break;
		        	case '101' :
		        		//$menuBut.css('display','none');
		        		$('wait_process_file').css('display','none');
	        			$('wait_read_file').css('display','none');
	        			$('processed_file').css('display','');
	        			$('my_file').css('display','none');
		        		titleText = '已办文件';
		        		break;
		        	case '1100' :
		        		//$menuBut.css('display','none');
		        		$('wait_process_file').css('display','none');
	        			$('wait_read_file').css('display','none');
	        			$('processed_file').css('display','none');
	        			$('my_file').css('display','');
		        		titleText = '我的文件';
		        		break;
		        	default :
		        		break;
	        	}
	        	workStatus = status;
	        	currPage = '0';
	        	offset = '0';
	        	hasMore = '';
	        	//loadList('update');
	        	CacheDataToPage();
	        	$page.setTimeout(function() {
	        	   //hint('列表自动刷新中...');
	        	   updateCache();
                }, 2000);
	        }

	        //列表查询方法
	        function searchList(){
	        	currPage = '0';
	        	offset = '0';
	        	hasMore = '';
	        	loadList('update');
	        }

	        // 打开新建流程列表
	        function openNewFlowList(){
	        	$page.open('../workflow/workflow_list.xml',{animation:'down-to-up'});
	        }
