			function downfrsh(){
				if(addMore=='1'){
					return;
				}
				curpage = parseInt(curpage)+1+"";
				$('bottom_more').css('display','none');
	            $('bottom_load').css('display','block');
				refresh();
			}

			function searchMail(){
				curpage = '1';
				$('bottom_more').css('display','none');
	            $('bottom_load').css('display','block');
				refresh();
			}

		   function upfrsh(){
		   		$('bottom_more').css('display','none');
	            $('bottom_load').css('display','none');
	            $('indexContent').showBottom();
		   		curpage = '1';
				if($('subject').value==''){
		   			updateCache2();
		   		}else{
		   			refresh();
		   		}
		   }

		   function toErro(error){
				if (error == 'timeout') {
					hint('连接服务器超时！');
				} else if (error == '404') {
					hint('链接超时重新登录！');
				} else if (error == '500') {
					hint('内部服务器错误！');
				} else {
					hint('访问网络错误！错误代码:' + error);
				}
			}

			//缓存无数据则更新
	        function updateCache(){
			      var dbName = storage.getItem('ezMobile_dbName');
       	    	  var db = $phone.openDatabase(dbName);
			      db.transaction(queryHasDataDB, function(err){alert(err);}, function(){});
			      function queryHasDataDB(tx) {
					tx.executeSql('select mailid from mail_data where mailType="receive" and userId="'+userId+'"',[],
						function(results){
							  if(results.rows!=null && results.rows.length>0){
							     CacheDataToPage();
							  }else{
							     updateCache2();
							  }
						} , function(err){alert(err);}); 
			      }
	        }

	        //更新缓存数据并刷新页面
	        function updateCache2(){
	           $http.post(
	       			evoUrl+'/ezOffice/mail/reListdate.do',
			        {currPage : '1'}, 
			        function(data) {
			            var obj = JSON.parse(data);
			            if(obj){
			        	  DbToCacheData(obj);
			        	  CacheDataToPage();
			        	}

			        },function(error) {
				    if (error == 'timeout') {
						hint('连接服务器超时！');
				    } else if (error == '404') {
						hint('链接超时重新登录！');
				    } else if (error == '500') {
						hint('内部服务器错误！');
				    } else {
						hint('访问网络错误！错误代码:' + error);
				    }
				  }

			  );
	        }

	        //将获取到的数据存入缓存
	        function DbToCacheData(obj) {
		       var dbName = storage.getItem('ezMobile_dbName');
       	       var db = $phone.openDatabase(dbName);
		       db.transaction(DbToCacheData_1);
		       function DbToCacheData_1(tx) {
			        tx.executeSql('delete from mail_data where mailType="receive" and userId="'+userId+'"');
			        var result = obj.message.result;
	        		if(result == '1'){
	        			var recordCount = obj.data.recordCount;
						if(parseInt(recordCount) >0){
		                	var list = obj.data.list;
		                	var hasMore='0';
		                	var pageCount = obj.data.pageCount;
		                	if(parseInt(pageCount)>1){
		                		hasMore='1';
		                	}
		                	var notRead = obj.data.notReadSize+"";
		                	if(notRead== null || notRead=="" ||notRead=="0" ||notRead=="null"){
								notRead="";
							}
							var picRootPath = obj.picRootPath;
		                	if(list instanceof Array){
		                		for(var i=0;i<list.length;i++){
		                			var mailid = list[i].mailid;
			                		var mailuserid = list[i].mailuserid;
			                		var read = list[i].NotRead;
			                		var mailanonymous = list[i].mailanonymous;
			                		var empLivingPhoto="";
		                			if(list[i].empLivingPhoto != "" && list[i].empLivingPhoto != "null"
		                			  &&list[i].empLivingPhoto != null && mailanonymous!="1"){
			        					empLivingPhoto = picRootPath+list[i].empLivingPhoto;
			        				}
		                			var mailposttime = list[i].mailposttime;
				                    if(mailposttime!=null&&mailposttime.length>20){
				                        mailposttime = mailposttime.substring(0,16);
				                    }
				                    var mailpostername = list[i].mailpostername;
				                    var mailsubject = list[i].mailsubject;
				                    var accessorySize = list[i].accessorySize;
				                    var dealfile_sql = 'insert into mail_data(mailid,mailuserid,read,photo,notRead,mailposttime,mailpostername,mailsubject,accessorySize,mailType,userId,hasMore) '+
	        							' values("'+mailid+'","'+mailuserid+'","'+read+'","'+empLivingPhoto+'","'+notRead+'","'+mailposttime+'","'+mailpostername+'","'+mailsubject+'","'+accessorySize+'","receive","'+userId+'","'+hasMore+'")';
	        						tx.executeSql(dealfile_sql);
		                		}
		                	}else if(list instanceof Object){
		                			var mailid = list.mailid;
			                		var mailuserid = list.mailuserid;
			                		var read = list.NotRead;
			                		var mailanonymous = list.mailanonymous;
			                		var empLivingPhoto="";
		                			if(list.empLivingPhoto != "" && list.empLivingPhoto != "null"
		                			  &&list.empLivingPhoto != null && mailanonymous!="1"){
			        					empLivingPhoto = picRootPath+list.empLivingPhoto;
			        				}
		                			var mailposttime = list.mailposttime;
				                    if(mailposttime!=null&&mailposttime.length>20){
				                        mailposttime = mailposttime.substring(0,16);
				                    }
				                    var mailpostername = list.mailpostername;
				                    var mailsubject = list.mailsubject;
				                    var accessorySize = list.accessorySize;
				                    var dealfile_sql = 'insert into mail_data(mailid,mailuserid,read,photo,notRead,mailposttime,mailpostername,mailsubject,accessorySize,mailType,userId,hasMore) '+
	        							' values("'+mailid+'","'+mailuserid+'","'+read+'","'+empLivingPhoto+'","'+notRead+'","'+mailposttime+'","'+mailpostername+'","'+mailsubject+'","'+accessorySize+'","receive","'+userId+'","'+hasMore+'")';
	        						tx.executeSql(dealfile_sql);
		                	}
						}else{
							noDataContent();
						}
	        		}else{
	        			//查询数据异常
	        			noDataContent();
	        		}
			   }
		    }

	        //没有查到数据或者数据查询异常时
	        function noDataContent(){
	        	var listJson = {items:[]};
	        	var itemJson = {
                    template : 2,
                    onclick : '',
                    widgets : {
                        title : {text :'暂无相关记录'}
                    }
                }
                listJson.items.push(itemJson);
				$('mailList').update(listJson);
				$('indexContent').hideBottom();
	        }

		    //将缓存数据更新到页面
		    function CacheDataToPage(){
	        	  $('mailList').clear();
	        	  arrId = new Array();
			      var dbName = storage.getItem('ezMobile_dbName');
       	          var db = $phone.openDatabase(dbName);
			      db.transaction(queryDealfileListDB, function(err){alert(err);}, function(){});
			      function queryDealfileListDB(tx) {
					tx.executeSql('select mailid,mailuserid,read,photo,notRead,mailposttime,mailpostername,mailsubject,accessorySize,hasMore from mail_data where mailType = "receive" and userId = "'+userId+'"',[],
						function(results){
							  if(results.rows!=null && results.rows.length>0){
							  	var listJson = {items:[]};
							    for(var i=0; i<results.rows.length; i++){
								      var mailid = results.rows.item(i).mailid;
								      var mailuserid = results.rows.item(i).mailuserid;
								      var read = results.rows.item(i).read;
								      var photo = results.rows.item(i).photo;
								      var mailposttime = results.rows.item(i).mailposttime;
								      var mailpostername = results.rows.item(i).mailpostername;
								      var mailsubject = results.rows.item(i).mailsubject;
								      var accessorySize = results.rows.item(i).accessorySize;
								      arrId.push(mailid);
								      var cont ='<font color="#b8b8b8" size="16">'+mailpostername+'</font> <font color="#555555" size="16">'+mailsubject+'</font>';
								      var readPic="mail_read.png";
			                		  if(read=="1"){
			                			  readPic = "mail_unread.png";
			                		  }
								      if(accessorySize=="0"){
								      	  var itemJson = {
							                    template : 0,
							                    id : 'ite'+mailid,
							                    onclick : 'toView("'+mailid+'","'+mailuserid+'")',
							                    widgets : {
							                    	unRead : {src:readPic},
							                        chData : {onclick:'addIds("'+mailid+'","'+mailuserid+'");',src:'mail_uncheck.png'},
							                        photo : {src:photo},
							                        cont : {text:cont},
							                        time : {text:mailposttime}
							                    }
							                }
						                listJson.items.push(itemJson);
								      }else{
								      	 var itemJson = {
							                    template : 1,
							                    id : 'ite'+mailid,
							                    onclick : 'toView("'+mailid+'","'+mailuserid+'")',
							                    widgets : {
							                    	unRead : {src:readPic},
							                        chData : {onclick:'addIds("'+mailid+'","'+mailuserid+'");',src:'mail_uncheck.png'},
							                        photo : {src:photo},
							                        cont : {text:cont},
							                        time : {text:mailposttime}
							                    }
							                }
						                listJson.items.push(itemJson);
								      }
							    }
							  $('mailList').addMore(listJson);
							  var notRead = results.rows.item(0).notRead;
							  if(notRead==""){
								  $('unreadMail').text="";
							  }else{
								  $('unreadMail').text="("+notRead+")";
							  }
							  var hasMore = results.rows.item(0).hasMore;
							  if(hasMore=='1'){
							  	 $('indexContent').showBottom();
							  	 $('bottom_more').css('display','block');
	                			 $('bottom_load').css('display','none');
							  }else{
							  	 $('indexContent').hideBottom();
							  }
							  $('indexContent').hideTop();
							}else{
								noDataContent();
							}
						} , function(err){alert(err);}); 
			      }
	        }
